mapper.select:
select 

from response a
where a.id = #{id,jdbcType=BIGINT}

mapper.update:
update response
set newInfo = #{newinfo,jdbcType=VARCHAR}
where id = #{id,jdbcType=BIGINT}

mapper.select:
select id, CASE WHEN demandType = '0' THEN '航线需求' WHEN demandType = '1' THEN '运力投放' END AS demandtypeStr, demandType, (select title from demand b where a.demand_id = b.id) title, releaseTime, CASE WHEN responseProgress = '0' THEN '意向征集' WHEN responseProgress = '1' THEN '订单确认' WHEN responseProgress = '2' THEN '已撤回' WHEN responseProgress = '3' THEN '需求关闭' WHEN responseProgress = '4' THEN '落选状态' WHEN responseProgress = '5' THEN '交易完成' WHEN responseProgress = '6' THEN '订单完成' WHEN responseProgress = '7' THEN '佣金支付' END AS responseProgressStr, responseProgress,demand_id,employee_id, (select employee_id from demand b where (b.id = a.demand_id AND intentionMoneyState = '1') UNION SELECT a.id FROM employee a WHERE a.whetherSign = '0' AND a.id = (select employee_id from demand b where b.id = a.demand_id)) demand_employee_id, (select count(*) from chat where toName_id = #{employeeId,jdbcType=BIGINT} and textType = '0' and state = '1') unread_num from response a
(demandType = '0' OR demandType = '1')
and (intentionMoneyState = '0' or (select c.whetherSign from employee c where c.id = #{employeeId,jdbcType=BIGINT})='0')
and demandType = #{demandType,jdbcType=VARCHAR}
and employee_id = #{employeeId,jdbcType=BIGINT}
and responseProgress = #{responseProgress,jdbcType=VARCHAR}
order by releaseTime ASC
order by releaseTime DESC
limit #{startIndex,jdbcType=INTEGER},#{pageSize,jdbcType=INTEGER}

  	select
  	count(*)
  	from response

  		(demandType = '0' OR demandType = '1')

			and demandType = #{demandType,jdbcType=VARCHAR}


			and employee_id = #{employeeId,jdbcType=BIGINT}


			and responseProgress = #{responseProgress,jdbcType=VARCHAR}
